const {writeFile} = require('./file-utils')

function convertObjectToTuples(obj) {
    return Object.entries(obj).map(([key, value]) => [key, value]);
}

function convertArrayToTuples(arr) {
    return arr.map((value, index) => [index, value]);
}

function convertValueOfObjectToTuples(obj) {
    return Object.values(obj).map((value) => value);
}
